import React, {useState} from 'react'
import { Link, NavLink } from 'react-router-dom';
import axios from 'axios';
import '../css/login.css'

const Login = () => {
    const [user, setUser] = useState({
        email: "",
        password: ""
    })

    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value
        })
    };

    const handleLogin = () => {
        axios.post("http://localhost:4000/login", user)
        .then(res => {
            if (res.data.message) {
                alert(res.data.message)
                setUser({
                    email: "",
                    password: ""
                })
            }
        })
        .catch(err => {
            console.log(err)
            alert("Error logging in")
        })
    }
    
    return (
            <div className='login'>
                <h1> Login </h1>
                <input type="email" name="email" value={user.email} placeholder='Enter your Email' onChange={handleChange}></input>
                <input type="password" name="password" value={user.password} placeholder='Enter your Password' onChange={handleChange}></input>
                <div className='button' onClick={handleLogin}> Login </div>
                <NavLink to='/'>Forgot Password</NavLink>
                <div> or </div>
                <Link className='button' aria-current="page" to="/register" > Register </Link>
            </div>
    )
}

export default Login
